import React, {Component, useState} from 'react';
import './Login.scss';
import { login } from '../util/APIUtils';
import { ACCESS_TOKEN } from '../constants';
import axios from 'axios';

const Login = () => {
    const [userInfo, setUserInfo] = useState(
        {
            email:'',
            password: '',
        }
    );
    const onChange = (e) => {setUserInfo({
        ...userInfo,
        [e.target.name]: e.target.value
    });}


    const handleSubmit = (event) => {
        event.preventDefault();   
        axios.post('http://localhost:5000/api/auth/signin', {
            "usernameOrEmail": userInfo.email,
            "password": userInfo.password
        })
        .then(response => {
            console.log(userInfo.email); 
            localStorage.setItem('accessToken', response.accessToken);
        
        },(error) => {
            if(error.status === 401) {
                console.log(userInfo.password); 
            } else {
                console.log(userInfo.email);                                     
            }
        });
    }

    return (
        <div className='loginScreen'>
            <div className='loginContainer'>
                <div className='loginTitle'>로그인</div>
                <form onSubmit={handleSubmit} className='loginForm'>
                    <div className='loginFormContainer'>
                        <div className='loginSubTitle'>이메일</div>
                        <input onChange={onChange} type='text' name='email' className='form-control'/>
                    </div>
                    <div className='loginFormContainer'>
                        <div className='loginSubTitle'>비밀번호</div>
                        <input  onChange={onChange} type='password' name='password' className='form-control'/>
                    </div>
                    <div className='loginFindContainer'>
                        <div className='loginFindInfo'>ID찾기 / 비밀번호 찾기</div>
                    </div>
                    <div className='loginButtonContainer'>
                    <button type='submit' className='loginButton'>LOGIN</button>
                </div>
                </form>
                <div className='loginSignUpContainer'>
                    <div className='loginSignUpText'>아직 아이디가 없으신가요?</div>
                    <div className='loginSignUp'>회원가입</div>
                </div>
            </div>
        </div>
    )
}

export default Login;
