import React, {useState} from 'react';
import { signup, checkUsernameAvailability, checkEmailAvailability } from '../../util/APIUtils';
import './SignUp.scss';
import axios from 'axios';
import { Form, Input, Button, notification } from 'antd';

const SignUp = () => {
    const [userInfo, setUserInfo] = useState({
            'name': '',
            'username': '',
            'email':'',
            'password': '',
    });
    const onChange = (e) => {setUserInfo({
        ...userInfo,
        [e.target.name]: e.target.value
    });}

    const handleSubmit = (e) => {
        e.preventDefault();
        const signupRequest = {
            email: userInfo.email,
            username: userInfo.username,
            password: userInfo.password
        };
        /*
        axios.post("http://localhost:5000/api/auth/signup", {
            "username": userInfo.username,
            "email": userInfo.email,
            "password": userInfo.password
        })*/
        signup(signupRequest)
            .then(res => {
                notification.success({
                    message: 'Cheeze Toon',
                    description: "성공적으로 회원가입되었습니다. 로그인을 해주세요.",
                });          
                this.props.history.push("/login");  
            },(error) => {
                notification.error({
                    message: 'Cheeze Toon',
                    description: error.message || '다시 시도해주세요.'
                });
            });
    }
    return (
        <div className='signUpScreen'>
            <div className='signUpContainer'>
                <div className='signUpTitle'>회원가입</div>
                <form onSubmit={handleSubmit} className='signUpForm'>
                    <div className='signUpFormContainer'>
                        <div className='signUpSubTitle'>이름</div>
                        <input onChange={onChange} type='text' name='name' className='signUpSubInput'/>
                    </div>
                    <div className='signUpFormContainer'>
                        <div className='signUpSubTitle'>닉네임</div>
                        <input onChange={onChange} type='text' name='username' className='signUpSubInput'/>
                    </div>
                    <div className='signUpFormContainer'>
                        <div className='signUpSubTitle'>이메일</div>
                        <input onChange={onChange} type='text' name='email' className='signUpSubInput'/>
                    </div>
                    <div className='signUpFormContainer'>
                        <div className='signUpSubTitle'>비밀번호</div>
                        <input onChange={onChange} type='password' name='password' className='signUpSubInput'/>
                    </div>
                    <div className='signUpButtonContainer'>
                        <button type='submit' className='signUpButton'>회원가입</button>
                    </div>
                </form>
            </div>
        </div>
    )
}
export default SignUp;
